        //     button: "ok!",
        //   })
          .then(()=> {
            fetchComments();
        });
        
        // id.preventDefault();


     }
          // fetch posts
          const [posts, setPosts] = useState([]);

          useEffect(() => {
            const fetchPosts=async()=>{
                const response=await fetch(`http://127.0.0.1:8000/api/fetch/${id}`)
                const data=await response.json()
        
                  .then(data => {
                      setPosts(data);
                      console.log(data);
