import React, { useEffect, useState,useFetch } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import axios from 'axios'
import swal from 'sweetalert';
import '../Posts.css';

function Blog() {

    const { id } = useParams();
    //insert comments
    const [comment, setComment] = useState("");
    const [costumer_id, setCostumer_id] = useState({});
    const [post_id, setPost_id] = useState({});
    const user_id =sessionStorage.getItem('user_id');
    console.log(id);
  
    const handleClick = async (e) => {
        e.preventDefault();
        const response = await fetch(`http://127.0.0.1:8000/api/comments_post`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ comment: comment, costumer_id:user_id, post_id:id }),

        })
        
        ;
        if (response.ok) {
            // window.alert("comment added successfully")
            swal({
                title: "Good job!",
                text: " comment added successfully!",
                icon: "success",
                button: "ok!",
              })
              .then(()=> {
                fetchComments();
            });
        }
    };
    const fetchComments = async () => {
        const response = await fetch(`http://127.0.0.1:8000/api/comments`)
        const res = await response.json()
        setAll(res)
    }

    console.log(comment)

    const [selectedPost, setSelectedPost] = useState([]);
    const [currentPost, setCurrentPost] = useState(1);


    //fetch all comments
    const [all, setAll] = useState([])
    useEffect(() => {
        const fetchComments = async () => {
            const response = await fetch(`http://127.0.0.1:8000/api/comments`)
            const res = await response.json()
            setAll(res)
        }

        fetchComments()
    }, [])
    console.log(all)



 

    // update comments
    const [update, setUpdate] = useState(false)
    const [editComment, setEditComment] = useState({
        comment:'',costumer_id:'',post_id:''
    })
    const updateHandler = (e) => {
        e.preventDefault();

        setUpdate(true);

    }
     useEffect(()=>{
        const getComment=async()=>{
            const response= await fetch (`http://127.0.0.1:8000/api/comments_post/${id}`)
            const dbData=await response.json();
            setEditComment(dbData);
            console.log(response);
           
            
        }
        getComment();
    },[update])
   
 
    const editHandeler = async(e) => {
        e.preventDefault();
      
       await axios.put(`http://127.0.0.1:8000/api/comment/${id}`, editComment)
            .then((res) => setEditComment(res.data))
            
        
        if (update) {
            window.alert('Your comment has been updated successfuly')

        }


    }

    console.log(editComment)
   

    const deleteHandeler = (id) => {
        axios.delete(`http://127.0.0.1:8000/api/comment/${id}`)     
        // swal({
        //     title: "Good job!",
        //     text: " comment deleted successfully!",
        //     icon: "success",
        //     button: "ok!",
        //   })
          .then(()=> {
            fetchComments();
        });
        
        // id.preventDefault();


     }
          // fetch posts
          const [posts, setPosts] = useState([]);

          useEffect(() => {
            const fetchPosts=async()=>{
                const response=await fetch(`http://127.0.0.1:8000/api/fetch/${id}`)
                const data=await response.json()
        
                  .then(data => {
                      setPosts(data);
                      console.log(data);
                  });
            }
          
            fetchPosts()     
          }, []);
          console.log(posts);
  return (
    
<section class="wpo-blog-single-section wpo-blog-single-left-sidebar-section section-padding">
            <div class="container">
                <div class="row">
                    <div class="col col-lg-8 col-12 order-lg-2">
                        <div class="wpo-blog-content">
                            <div class="post format-standard-image">
                                <div class="entry-media">
                                    {/* <blockquote>
                                    Combined with a handful of model sentence structures, generate Lorem Ipsum which looks reasonable. The generated Lorem Ipsum is therefore always free from repetition, injected humour, or non-characteristic words etc.
                                </blockquote><img src="assets/images/blog/img-4.jpg" alt/> */}
                                </div>
                                <div class="entry-meta">
                                    
                                    <ul>
                                        <li><i class="fi flaticon-user"></i> By <a href="#">{posts.name}</a> </li>
                                        <li><i class="fi flaticon-comment-white-oval-bubble"></i> Comments  </li>
                                        <li><i class="fi flaticon-calendar"></i> {posts.Date}</li>
                                    </ul>
                                </div>
                                <h5></h5>
                                <h4>{posts.post}</h4>
                                
                                {/* <p>I must explain to you how all this mistaken idea of denouncing pleasure and praising pain was born and I will give you a complete account of the system, and expound the actual teachings of the great explorer of the truth, the master-builder of human happiness. No one rejects, dislikes, or avoids pleasure itself,</p> */}

                                {/* <div class="gallery">
                                    <div>
                                        <img src="assets/images/blog-details/1.jpeg" alt=""/>
                                    </div>
                                    <div>
                                        <img src="assets/images/blog-details/2.jpg" alt=""/>
                                    </div>
                                </div> */}
                            </div>

                            <div class="tag-share clearfix">
                                <div class="tag">
                                    <span>Share: </span>
                                    <ul>
                                        <li><a href="#">Planning</a></li>
                                        <li><a href="#">photography</a></li>
                                        <li><a href="#"> creative</a></li>
                                    </ul>
                                </div>
                            </div>
                             {/* <!-- end tag-share --> */}
                            <div class="tag-share-s2 clearfix">
                                <div class="tag">
                                    <span>Share: </span>
                                    <ul>
                                        <li><a href="#">facebook</a></li>
                                        <li><a href="#">twitter</a></li>
                                        <li><a href="#">linkedin</a></li>
                                        <li><a href="#">pinterest</a></li>
                                    </ul>
                                </div>
                            </div>
                             {/* <!-- end tag-share --> */}

                            {/* <div class="author-box">
                                <div class="author-avatar">
                                    <a href="#" target="_blank"><img src="assets/images/blog-details/author.jpg" alt/></a>
                                </div>
                                <div class="author-content">
                                    <a href="#" class="author-name">Author: Jenny Watson</a>
                                    <p>Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam, eaque ipsa quae ab illo inventore veritatis.</p>
                                    <div class="socials">
                                        <ul class="social-link">
                                            <li><a href="#"><i class="ti-facebook"></i></a></li>
                                            <li><a href="#"><i class="ti-twitter-alt"></i></a></li>
                                            <li><a href="#"><i class="ti-linkedin"></i></a></li>
                                            <li><a href="#"><i class="ti-instagram"></i></a></li>
                                        </ul>
                                    </div>
                                </div>
                            </div> */}
                             {/* <!-- end author-box --> */}
                             <div class="comments-area">
                                <div class="comments-section">
                                    <h3 class="comments-title"> Comments</h3>
                                    <ol class="comments">
                                        <li class="comment even thread-even depth-1" id="comment-1">
                                        {all.length > 0 && all.map((user) => {
                                                    // if (user.post_id !== currentPost) return;
                                                    return (
                                            <div id="div-comment-1">
                                           
                                                <div class="comment-theme">
                                                    <div class="comment-image"><img src={`http://127.0.0.1:8000/${user.picture}`} alt height="70px" width="70px" /></div>
                                                </div>
                                                <div class="comment-main-area">
                                                    <div class="comment-wrapper">
                                                        <div class="comments-meta">
                                                            <h4>{user.name}  <span class="comments-date">says {user.Date}</span></h4>
                                                        </div>
                                                        <div class="comment-area">
                                                          
                                                        {!update ?
                                                                            <p>{user.comment}</p>

                                                                            : null}
                                                                        {update ?
                                                                            <input name='comment' style={{  background: "none" }} onChange={(e)=>setEditComment({...editComment,comment:e.target.value})}  defaultValue={user.comment}/>

                                                                            : null}
                                                                        <div className="comments-reply">
                                                                            <a className="comment-reply-link" href="#"><span>Reply</span></a>
                                                                            {!update ?
                                                                                <button onClick={updateHandler} style={{ border: "none", background: "none" }}><a className="comment-reply-link" href=""><span>edit</span></a></button>

                                                                                : null}
                                                                            {update ?
                                                                                <button onClick={()=>editHandeler(user.id)} style={{ border: "none", background: "none" }} type='submit'><a className="comment-reply-link" href=""><span>edit</span></a></button>

                                                                                : null}
                                                                            <button type='submit' onClick={()=>deleteHandeler(id)} style={{ border: "none", background: "none" }}><a className="comment-reply-link" href="#"><span>Delete</span></a></button>

                                                                        </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                            )
                                        })}
                                            <ul class="children">
                                                <li class="comment">
                                                    <div>
                                                        <div class="comment-theme">
                                                            <div class="comment-image"><img src="assets/images/blog-details/comments-author/img-2.jpg" alt/></div>
                                                        </div>
                                                        <div class="comment-main-area">
                                                            <div class="comment-wrapper">
                                                                <div class="comments-meta">
                                                                    <h4>John Abraham  <span class="comments-date">says Jul 21, 2021 at 10:00am</span></h4>
                                                                </div>
                                                                <div class="comment-area">
                                                                    <p>I must explain to you how all this mistaken idea of denouncing pleasure and praising pain was born and I will give you a complete account of the system</p>
                                                                    <div class="comments-reply">
                                                                        <a  class="comment-reply-link" href="#"><span>Reply</span></a>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <ul>
                                                        <li class="comment">
                                                            <div>
                                                                <div class="comment-theme">
                                                                    <div class="comment-image"><img src="assets/images/blog-details/comments-author/img-3.jpg" alt/></div>
                                                                </div>
                                                                <div class="comment-main-area">
                                                                    <div class="comment-wrapper">
                                                                        <div class="comments-meta">
                                                                            <h4>Robert Sonny <span class="comments-date">says Jul 21, 2021 at 10:00am</span></h4>
                                                                        </div>
                                                                        <div class="comment-area">
                                                                            <p>I must explain to you how all this mistaken idea of denouncing pleasure and praising pain was born and I will give you a complete account of the system</p>
                                                                            <div class="comments-reply">
                                                                                <a  class="comment-reply-link" href="#"><span>Reply</span></a>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </li>
                                                    </ul>
                                                </li>
                                            </ul>
                                        </li>

                                        <li class="comment">
                                            <div>
                                                <div class="comment-theme">
                                                    <div class="comment-image"><img src="assets/images/blog-details/comments-author/img-1.jpg" alt/></div>
                                                </div>
                                                <div class="comment-main-area">
                                                    <div class="comment-wrapper">
                                                        <div class="comments-meta">
                                                            <h4>John Abraham  <span class="comments-date">says Jul 21, 2021 at 10:00am</span></h4>
                                                        </div>
                                                        <div class="comment-area">
                                                            <p>I must explain to you how all this mistaken idea of denouncing pleasure and praising pain was born and I will give you a complete account of the system</p>
                                                            <div class="comments-reply">
                                                                <a  class="comment-reply-link" href="#"><span>Reply</span></a>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </li>
                                    </ol>
                                </div> 
                                {/* <!-- end comments-section --> */}

                                <div class="comment-respond">
                                    <h3 class="comment-reply-title">Leave a reply</h3>
                                    <form class="comment-form">
                                        {/* <div class="form-inputs">
                                            <input placeholder="Name" type="text"/>
                                            <input placeholder="Email" type="email"/>
                                            <input placeholder="Website" type="url"/>
                                        </div> */}
                                        <div class="form-textarea">
                                            <textarea id="comment" placeholder="Write Your Comments..."></textarea>
                                        </div>
                                        <div class="form-submit">
                                            <input id="submit" value="Post Comment" type="submit"/>
                                        </div>
                                    </form>
                                </div>
                            </div>

                            <div class="more-posts">
                                <div class="previous-post">
                                    <a href="blog.html">
                                        <span class="post-control-link">Previous Post</span>
                                        <span class="post-name">At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis praesentium.</span>
                                    </a>
                                </div>
                                <div class="next-post">
                                    <a href="blog-left-sidebar.html">
                                        <span class="post-control-link">Next Post</span>
                                        <span class="post-name">Dignissimos ducimus qui blanditiis praesentiu deleniti atque corrupti quos dolores</span>
                                    </a>
                                </div>
                            </div>

     
                             {/* <!-- end comments-area --> */}
                        </div>
                    </div>
                    <div class="col col-lg-4 order-lg-1">
                        <div class="blog-sidebar">
                            <div class="widget about-widget">
                                <div class="img-holder">
                                    <img src={`http://127.0.0.1:8000/${posts.picture}`} alt/>
                                </div>
                                <h4>{posts.name}</h4>
                                <p>Hi! beautiful people. I`m an authtor of this blog. Read our post - stay with us</p>
                                <div class="social">
                                    <ul class="clearfix">
                                        <li><a href="#"><i class="ti-facebook"></i></a></li>
                                        <li><a href="#"><i class="ti-twitter-alt"></i></a></li>
                                        <li><a href="#"><i class="ti-linkedin"></i></a></li>
                                        <li><a href="#"><i class="ti-pinterest"></i></a></li>
                                    </ul>
                                </div>
                                <div class="aw-shape">
                                    <img src="assets/images/blog/ab.png" alt=""/>
                                </div>
                            </div>
                            <div class="widget search-widget">
                                <form>
                                    <div>
                                        <input type="text" class="form-control" placeholder="Search Post.."/>
                                        <button type="submit"><i class="ti-search"></i></button>
                                    </div>
                                </form>
                            </div>
                            <div class="widget category-widget">
                                <h3>Categories</h3>
                                <ul>
                                    <li><a href="blog.html">Themes <span>5</span></a></li>
