    //         setUsers(data)
    //     }
    //     fetchUsers()
    // },[])
    // console.log(users)

    // update comments
    const [update, setUpdate] = useState(false)
    const updateHandler = (e) => {
        e.preventDefault();

        setUpdate(true);

    }

    const editHandeler = (e) => {
        e.preventDefault();
        axios.put(`http://127.0.0.1:8000/api/comment/${id}`, { comment: comment, costumer_id, post_id })
            .then((res) => setUpdate(res.data))
        
        console.log(comment)
        if (!update) {
            window.alert('Your comment has been updated successfuly')

        }


    }
    const [selectedComment,setSelectedComment]=useState([])
   
        // const getComment = async () => {
        //     const response = await fetch(`http://127.0.0.1:8000/api/comment/${id}`)
        //     const res = await response.json()
        //     setSelectedComment(res)
        //     console.log(res);

        // }

        
   
    // const getComment = () => {
    //     axios.get(`http://127.0.0.1:8000/api/comment/${id}`)
    //         .then((res) => {
    //             setSelectedComment(res.data);
    //             console.log(res.data);
    //         })
    // }

    const deleteHandeler = (id) => {
        axios.delete(`http://127.0.0.1:8000/api/comment/${id}`)     
        //    console.log(id)

            // .then(() => {
            //     getComment();
            // })
     }

    return (
