                                        </a>
                                    </div>
                                </div>

                                <div className="comments-area">
                                    <div className="comments-section">
                                        <h3 className="comments-title">5 Comments</h3>
                                        <ol className="comments">
                                            <li className="comment even thread-even depth-1" id="comment-1">
                                                {all.length > 0 && all.map((user) => {
                                                    if (user.post_id !== currentPost) return;
                                                    return (
                                                        <div id="div-comment-1">
                                                            <div className="comment-theme">
                                                                <div className="comment-image"><img src="assets/images/blog-details/comments-author/img-1.jpg" alt /></div>
                                                            </div>

                                                            <div className="comment-main-area">

                                                                <div className="comment-wrapper">

                                                                    <div className="comments-meta">
                                                                        <h4>{user.name} <span className="comments-date">{user.Date}</span></h4>
                                                                    </div>
                                                                    <div className="comment-area">


                                                                        {!update ?
                                                                            <p>{user.comment}</p>

                                                                            : null}
                                                                        {update ?
                                                                            <input name='comment' style={{ border: "none", background: "none" }} onChange={(e) => setComment({ ...all, comment: e.target.value })} value={user.comment} />

                                                                            : null}
                                                                        <div className="comments-reply">
                                                                            <a className="comment-reply-link" href="#"><span>Reply</span></a>
                                                                            {!update ?
                                                                                <button onClick={updateHandler} style={{ border: "none", background: "none" }}><a className="comment-reply-link" href="#"><span>edit</span></a></button>

                                                                                : null}
                                                                            {update ?
                                                                                <button onClick={editHandeler} style={{ border: "none", background: "none" }} type='submit'><a className="comment-reply-link" href="#"><span>update</span></a></button>

                                                                                : null}
                                                                            <button type='submit' onClick={deleteHandeler(user.id)} style={{ border: "none", background: "none" }}><a className="comment-reply-link" href="#"><span>Delete</span></a></button>

